import React, { Fragment, useEffect } from 'react';
import { useSelector } from 'react-redux';

import ApexCharts from 'apexcharts';
import Chart from "react-apexcharts";

export default function ChartTemperature() {

    const { weatherNextDays } = useSelector(state => state.WeatherReducer)



    const monthNames = ["Jan", "Feb", "Mar", "Apr", "May", "Jun",
  "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];





    let mangdate = [];
    let mangWeather = [];
    let mangTemp = [];


    if (weatherNextDays) {
        weatherNextDays.map((weather,index) => {

            if (weather.dt_txt.slice(11) == "12:00:00") {
              // PUSH DATE
              const DATE = new Date(weather.dt_txt)
              const date = String(DATE.getDate()).padStart(2, '0');
              const monthLetter = monthNames[DATE.getMonth()]
              const dateText = `${date} ${monthLetter}`
              mangdate.push(dateText)


              // PUSH MANG WEATHER
              mangWeather.push(weather)

              // PUSH TEMP VALUE
              mangTemp.push(Math.round(Number(weather.main.temp) - 273.15))
            }

          });

    }



    if (weatherNextDays) {
        const series = {
            temperature: {
                value: mangTemp,
                dates: mangDate
            },
        }

        var options = {
            series: [{
                name: "TEMPERATURE",
                data: series.temperature.value
            }],
            chart: {
                type: 'area',
                height: 350,
                zoom: {
                    enabled: true
                }
            },
            dataLabels: {
                enabled: true
            },
            stroke: {
                curve: 'smooth'
            },
            title: {
                text: 'Unit: °C',
                align: 'left'
            },
            subtitle: {
                text: 'Comparison data at 12:00:00 on each day',
                align: 'left'
            },
            labels: series.temperature.dates,

            yaxis: {
                opposite: true
            },
            legend: {
                horizontalAlign: 'right'
            }
        };
    }



    return (
        <Fragment>
            <Chart 
                options={options}
                series={series}
            />
            {/* {renderChartTemp(mangTemp, mangdate)} */}
        </Fragment>
    )
}
