    let [cityName, setCityName] = useState ("London")

    const dispatch = useDispatch();

    const {weatherToday} = useSelector(state => state.WeatherReducer)

    const handleChange = (event) => {
        let {name, value} = event.target;
        setCityName({
            [name]: value
        })
    }



