const initialState = {
    weatherToday : {},
    weatherNextDays: [],
}

export const WeatherReducer = (state = initialState, action) => {
    switch (action.type) {

        case "GET_DATA_TODAY": {
            state.weatherToday = action.weatherToday;

            return { ...state}
        }


        case "GET_DATA_NEXT_DAYS": {
            state.weatherNextDays = action.weatherNextDays;
            return { ...state}
        }

        default: {
            return { ...state}
        }
    }
}
