import Axios from "axios";

export class WeatherServices {
    // GET API NOW
    getWeatherToday = (city) => {
        const newName = city.toLowerCase().replace("city", "").trim();
        return Axios({
            url: `https://api.openweathermap.org/data/2.5/weather?q=${newName}&appid=2476c6f9e32c4dc831f18dc25d606e66`,
            method: "GET"
        })
    };

    // GET API NEXT DAYS
    getWeatherNextDays = (city) => {
        const newName = city.toLowerCase().replace("city", "").trim();
        return Axios({
            url: `https://api.openweathermap.org/data/2.5/forecast?q=${newName}&appid=2476c6f9e32c4dc831f18dc25d606e66`,
            method: "GET"
        })
    };
}

export const weatherServices = new WeatherServices();
